// Importación de React y hook useState para manejo de estado
import React, { useState } from "react";

// Monaco Editor: componente para crear el editor de texto estilo VSCode
import Editor from "@monaco-editor/react";

// Estilos de la app
import "./App.css";

function App() {
  // Estado para almacenar los comandos ingresados en el editor
  const [commandInput, setCommandInput] = useState("");

  // Estado para mostrar la salida de la ejecución
  const [output, setOutput] = useState("");

  // ----------------------------------------------
  // FUNCIÓN: Cargar archivo .smia desde el sistema
  // ----------------------------------------------
  const handleFileUpload = (e) => {
    const file = e.target.files[0]; // Obtiene el archivo
    if (file && file.name.endsWith(".smia")) {
      const reader = new FileReader(); // Lector de archivos
      reader.onload = (event) => {
        // Al terminar la lectura, guarda el contenido en el editor
        setCommandInput(event.target.result);
      };
      reader.readAsText(file); // Lee como texto plano
    } else {
      alert("Por favor selecciona un archivo .smia válido");
    }
  };

  // -----------------------------------------------------
  // FUNCIÓN: Ejecutar comandos enviándolos al backend
  // -----------------------------------------------------
  const handleExecute = async () => {
    try {
      const response = await fetch("http://localhost:3001/execute", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ comandos: commandInput }), // Se envían los comandos como JSON
      });

      const result = await response.text(); // Se recibe la respuesta como texto plano
      setOutput(result); // Se muestra en el área de salida
    } catch (error) {
      setOutput("Error al comunicarse con el backend.");
    }
  };

  // ----------------------------------------
  // FUNCIÓN: Limpiar la entrada y la salida
  // ----------------------------------------
  const handleClear = () => {
    setCommandInput("");
    setOutput("");
  };

  // ---------------------------------------------------
  // FUNCIÓN: Generar reporte de salida como archivo .txt
  // ---------------------------------------------------
  const handleGenerateReport = () => {
    const blob = new Blob([output], { type: "text/plain;charset=utf-8" });
    const url = URL.createObjectURL(blob);

    const link = document.createElement("a");
    link.href = url;
    link.download = "reporte_salida.txt"; // Nombre del archivo
    document.body.appendChild(link);
    link.click(); // Simula clic para descargar
    document.body.removeChild(link);
  };

  // ---------------------
  // RENDERIZADO PRINCIPAL
  // ---------------------
  return (
    <div className="App">
      {/* Encabezado del proyecto */}
      <header className="App-header">
        <h1>Sistema de Archivos EXT2 - Proyecto MIA</h1>
      </header>

      {/* Botones de control */}
      <div className="controls">
        <input type="file" accept=".smia" onChange={handleFileUpload} />
        <button onClick={handleExecute}>Ejecutar</button>
        <button onClick={handleClear}>Limpiar</button>
        <button onClick={handleGenerateReport}>Generar Reporte</button>
      </div>

      {/* Área dividida: Entrada y Salida */}
      <div className="editor-container">
        {/* Entrada de comandos con editor */}
        <div className="editor">
          <label>Entrada:</label>
          <Editor
            height="560px"
            language="javascript" // Puedes cambiar a "plaintext"
            theme="hc-black"      // Tema oscuro con alto contraste
            value={commandInput}
            onChange={(value) => setCommandInput(value)}
            options={{
              minimap: { enabled: false },
              fontSize: 14,
              lineNumbers: "on",
              scrollBeyondLastLine: false,
              wordWrap: "on",
            }}
          />
        </div>

        {/* Salida de los comandos ejecutados */}
        <div className="editor">
          <label>Salida:</label>
          <textarea
            className="salida"
            value={output}
            readOnly
            placeholder="# Aquí se verán los mensajes de la ejecución"
          />
        </div>
      </div>
    </div>
  );
}

// Exportación del componente App
export default App;
