import React, { useState } from "react";

function LoginForm({ onLogin }) {
  const [partitionId, setPartitionId] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberUser, setRememberUser] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!partitionId || !username || !password) {
      alert("Todos los campos son obligatorios.");
      return;
    }

    onLogin({
      partitionId,
      username,
      rememberUser,
    });
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Iniciar Sesi칩n</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>ID Partici칩n:</label>
        <input
          type="text"
          placeholder="Ej: 341A"
          value={partitionId}
          onChange={(e) => setPartitionId(e.target.value)}
          style={styles.input}
        />

        <label style={styles.label}>Usuario:</label>
        <input
          type="text"
          placeholder="root"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
        />

        <label style={styles.label}>Contrase침a:</label>
        <input
          type="password"
          placeholder="******"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={styles.input}
        />

        <div style={styles.checkboxContainer}>
          <input
            type="checkbox"
            id="recordar"
            checked={rememberUser}
            onChange={(e) => setRememberUser(e.target.checked)}
          />
          <label htmlFor="recordar" style={styles.checkboxLabel}>
            Recordar usuario
          </label>
        </div>

        <button type="submit" style={styles.button}>
          Submit
        </button>
      </form>
    </div>
  );
}

// 游꿛 Estilos suaves azul-verde
const styles = {
  container: {
    backgroundColor: "#f5fbfc", // azul verdoso muy claro
    padding: "2rem",
    borderRadius: "12px",
    width: "320px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
    textAlign: "left",
    fontFamily: "Segoe UI, sans-serif",
    color: "#1d3c42",
  },
  title: {
    textAlign: "center",
    marginBottom: "1.5rem",
    color: "#145f63", // verde azulado suave
  },
  label: {
    fontWeight: 600,
    fontSize: "0.95rem",
    marginBottom: "0.25rem",
    display: "block",
  },
  input: {
    width: "100%",
    padding: "0.5rem",
    marginBottom: "1rem",
    borderRadius: "6px",
    border: "1px solid #c0e0e4",
    backgroundColor: "#eaf7f9", // azul p치lido
    fontSize: "1rem",
  },
  checkboxContainer: {
    display: "flex",
    alignItems: "center",
    marginBottom: "1.2rem",
  },
  checkboxLabel: {
    marginLeft: "0.5rem",
    fontSize: "0.9rem",
  },
  button: {
    width: "100%",
    padding: "0.6rem",
    backgroundColor: "#4da6b3", // azul verdoso pastel
    color: "blue",
    fontWeight: "bold",
    border: "none",
    borderRadius: "6px",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "background-color 0.2s ease-in-out",
  },
};

export default LoginForm;
